import React, { Fragment, Component } from 'react';
import Home from './components/Home'
import Contatti from './components/Contatti'
import Servizio from './components/Servizio'
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom'
import './App.css';
import Nav from './components/Nav'
import axios from 'axios'

export class App extends Component {
  state = {
    menu: [],
    isLoaded: false
  }
  componentDidMount() {
    // posso omettere tutto l'url grazie al proxy nel package.json
    axios.get('/wordpress/wp-json/menus/v1/menus/menu1')
      .then(res => {
        var data = res.data.items
        var array = []
        data.map((ele, index) => {
          var oggetto = {}
          var titolo = ele.title
          var url = ele.url
          oggetto.titolo
          oggetto.url
          array.push(oggetto)





        })
        console.log(array)
        /* this.setState({
          menu: [{ titolo: titolo, url: url }],
          isLoaded: true
        }) */


      })
      .catch(err => console.log(err));
  }
  render() {



    return (
      <Router>
        <div className="App">
          <Nav />
          <Switch>
            <Route exact path="/" component={Home} />
            <Route exact path="/servizio" component={Servizio} />
            <Route exact path="/contatti" component={Contatti} />
          </Switch>



        </div>
      </Router>
    );

  }

}

export default App;
