import React, { Component } from 'react';
import axios from 'axios'
import PropTypes from 'prop-types'

export class ContradaItem extends Component {
    state = {
        imgUrl: "",
        author: "",
        isLoaded: false

    }
    static propTypes = {
        contrada: PropTypes.object.isRequired
    }
    componentDidMount() {
        const { features_media, author } = this.props.contrada
        const getImageUrl = axios.get(`/wp-json/wp/v2/media/${features_media}`)
        const getAuthor = axios.get(`/wp-json/wp/v2/users/${author}`)
        Promise.all([getImageUrl, getAuthor]).then(res => {
            console.log('questa è a risposta: ', res)
            this.setState({
                imgUrl: res[0].data.media_details.sizes.full.source_url,
                author: res[1].data.name,
                isLoaded: true

            })
        });
    }
    render() {
        const { title, excerpt } = this.props.contrada
        return (
            <div>
                <h2>{title.rendered}</h2>
                <div dangerouslySetInnerHTML={{ __html: excerpt.rendered }} />
            </div>
        )
    }
}

export default ContradaItem
